properties.script:
try{
	var d2 = responseMap.get('d2').toString();
	logger.info(d2);

	logger.info("response map "+d2);

	var dcm_fname = d2.substring(51); // /home/hafizh/modality/US/dicom/US_09-12-2017-0019_20180214163530.dcm
	logger.info("DCMFILE:"+dcm_fname);

	var img_fname = $('ImageFilename');
	logger.info("FILENAME:"+img_fname);

	var img_path = $('ImageFilePath');

	var output = img_path +'/'+ img_fname;
	logger.info("JPGOUT:"+output);


	/*
	*	Invoking dcmj2pnm command provided by dcmtk - Dicom Toolkit
	*   	Cmd : dcmj2pnm +Fa <dcm-file> +oj <image-file>
	*	Parameters : 
	*		+Fa       All frames will be extracted
	* 		+oj		JPEG format output
	*		
	*	Note: Image path is configurable at Generate DICOM File destination
	* 	See "Image File path" and "Image Filename" at Edit Transformer to alter
	*
	*/
	var exec = java.lang.Runtime.getRuntime().exec(['dcmj2pnm', '+Fa', dcm_fname,'+oj', output])
	logger.info('Frames start extracting.');	
	exec.waitFor();
	logger.info('Extracting finished.')
}
catch(err){
	logger.error(err);	
}

