com.mirth.connect.plugins.javascriptstep.script:
function executeQuery(query,queryParameters){
	var gateway_database = globalChannelMap.get('gateway_database');
	var user = globalChannelMap.get('gateway_database_user');
	var pass = globalChannelMap.get('gateway_database_pass');
	var dbConn;
	try {
		dbConn = DatabaseConnectionFactory.createDatabaseConnection('com.mysql.jdbc.Driver','jdbc:mysql://'+gateway_database,user,pass);
		var result = dbConn.executeCachedQuery(query,queryParameters);
		return result;
	} finally {
		if (dbConn) { 
			dbConn.close();
		}
	}
}

function executeInsertQuery(query,queryParameters){
	var gateway_database = globalChannelMap.get('gateway_database');
	var user = globalChannelMap.get('gateway_database_user');
	var pass = globalChannelMap.get('gateway_database_pass');
	var dbConn;
	try {
		dbConn = DatabaseConnectionFactory.createDatabaseConnection('com.mysql.jdbc.Driver','jdbc:mysql://'+gateway_database,user,pass);
		var result = dbConn.executeUpdate(query,queryParameters);
		return result;
	} finally {
		if (dbConn) { 
			dbConn.close();
		}
	}
}

function finalResponse(jsonString){
	channelMap.put('responseContent', jsonString);
	channelMap.put('responseContentType', 'application/fhir+json');
}

function parameterTest(bool){
	if(bool!==null){
		return true;
	} else {
		return false;
	}
}

globalMap.put('functExecuteQuery', validate(executeQuery, '', new Array()));
globalMap.put('functExecuteInsertQuery', validate(executeInsertQuery, '', new Array()));
globalMap.put('functFinalResponse', validate(finalResponse, '', new Array()));
globalMap.put('functParameterTest',  validate(parameterTest, '', new Array()));

var contextPath = sourceMap.get('contextPath').toString();
var prefixService = "/integration";
var params = contextPath.substring(prefixService.length).split('/');

var route = String(params[1]);
var routePath = String(params[2]);

switch(route){
	case 'orders':
		switch(routePath){
			case 'updateorder':
				destinationSet.removeAllExcept([10,15]);
			break;
			case 'complete-order':
				destinationSet.removeAllExcept([11,15]);
			break;
			case 'discontinued-order':
				destinationSet.removeAllExcept([12,15]);
			break;
			case 'neworder':
				destinationSet.removeAllExcept([1,15]);
			break;
			case 'orderlist':
				destinationSet.removeAllExcept([15,3]);
			break;
			case 'ultrasound':
				destinationSet.removeAllExcept([6,15]);
			break;
			case 'completeorder':
				destinationSet.removeAllExcept([14,15,16]);
			break;
			case 'order-media':
				destinationSet.removeAllExcept(17);
			break;
			case 'order-summary':
				destinationSet.removeAllExcept(19);
			break;
		}
	break;
	default:
	break;
}

