function executeQuery(query,queryParameters){
	var gateway_database = globalChannelMap.get('gateway_database');
	var user = globalChannelMap.get('gateway_database_user');
	var pass = globalChannelMap.get('gateway_database_pass');
	var dbConn;
	try {
		dbConn = DatabaseConnectionFactory.createDatabaseConnection('com.mysql.jdbc.Driver','jdbc:mysql://'+gateway_database,user,pass);
		var result = dbConn.executeCachedQuery(query,queryParameters);
		return result;
	} finally {
		if (dbConn) { 
			dbConn.close();
		}
	}
}

