		var responseData;
		var meta = '{"lastUpdated":"'+ new Date().toISOString()+'"}';
		responseData = '{"resourceType":"bundle",';
		responseData += '"meta":' + meta + ', ';
		responseData += '"type":"searchset", ';
		responseData += '"total":"' + countResult + '", ';
		responseData += '"link":"", ';
		responseData += '"entry": '+ images ;
		responseData += '}';
		$('functFinalResponse')(responseData);

}

if(sourceMap.get('method')=='GET'){
	var query = 'SELECT p.patient_name, p.identification_no, ';
	query += 'DATE_FORMAT(p.date_of_birth,"%d %b %Y") AS date_of_birth, p.patient_mrn, ';
	query += 'CASE ';
	query += 'WHEN p.gender = "M" THEN "MALE" ';
	query += 'WHEN p.gender = "F" THEN "FEMALE" ';
	query += 'WHEN p.gender = "O" THEN "OTHER" ';
	query += 'END AS gender, p.date_of_birth as clean_dob, p.gender as clean_gender, p.id as patient_id '
	query += 'FROM his_patient p';
	var queryParameters = [];

	if($('functParameterTest')(sourceMap.get('parameters').getParameter('patient_id'))){
		query += (queryParameters.length==0) ? ' WHERE ' : ' AND ';
		query += 'p.id = ? ';
		queryParameters.push(sourceMap.get('parameters').getParameter('patient_id'));
	}
	var result = $('functExecuteQuery')(query,queryParameters);

	var countResult = result.size();
	var count = 1;
	var patient = '';
	while (result.next()){
		patient += '{';
		patient += '"patient_name":"'+result.getString(1)+'",';
		patient += '"identification_no":"'+result.getString(2)+'", ';
		patient += '"date_of_birth":"'+result.getString(3)+'",'
		patient += '"patient_mrn":"'+result.getString(4)+'", ';
		patient += '"gender":"'+result.getString(5)+'", ';
		patient += '"clean_dob":"'+result.getString(6)+'", ';
		patient += '"clean_gender":"'+result.getString(7)+'", ';
		patient += '"patient_id":"'+result.getString(8)+'" ';
		patient += '}';
		if(count<countResult){
			addtolist += ', ';
		}
		count++;
		};

	var responseData;
	var meta = '{"lastUpdated":"'+ new Date().toISOString()+'"}';
		responseData = '{"resourceType":"bundle",';
		responseData += '"meta":' + meta + ', ';
		responseData += '"type":"searchset", ';
		responseData += '"total":"' + countResult + '", ';
		responseData += '"link":"", ';
		responseData += '"entry": '+ patient +'';
		responseData += '}';
		$('functFinalResponse')(responseData);
}

